// Referencias
const randomBtn = document.getElementById("randomBtn");
const resultBox = document.getElementById("consoleResult");
const consoleName = document.getElementById("consoleName");
const consoleDesc = document.getElementById("consoleDesc");
const consoleImg = document.getElementById("consoleImg");

// Evento
randomBtn.addEventListener("click", () => {

    // Elegir consola aleatoria
    const random = consolas[Math.floor(Math.random() * consolas.length)];

    // Insertar datos
    consoleName.textContent = random.nombre;
    consoleDesc.textContent = random.desc;
    consoleImg.src = random.img;

    // Mostrar tarjeta
    resultBox.style.display = "block";
});
